$(document).ajaxComplete(function(c,b){var a=b.getResponseHeader("X-Message-Type");var d=b.getResponseHeader("X-Message");if(d){$("#flash-message").html('<div class="flash '+a+'">'+d+"</div>")}});$(document).ready(function(){$("#signup").bind("click",function(){var a=$(this).attr("link");id=a.substr(a.lastIndexOf("/")+1);var b=$.ajax({url:"/game/"+id,type:"put"});b.success(function(){$("#playerList").load("/game/"+id+"/playerList");$("#waitingList").load("/game/"+id+"/waitingList")})})});$(document).ready(function(){$("#remove").bind("click",function(){var b=$(this).attr("link");id=b.substr(b.lastIndexOf("/")+1);var a=$.ajax({url:"/game/"+id,type:"delete"});a.success(function(){$("#playerList").load("/game/"+id+"/playerList");$("#waitingList").load("/game/"+id+"/waitingList")})})});$(function(){$("#datepicker").datetimepicker({dateFormat:"yy-mm-dd",hour:19,minute:30,showOn:"button",buttonImage:"images/calendar.gif",buttonImageOnly:true})});